var SlideText = ({ children, ...props }) => (
  <text
    alignmentBaseline="central"
    fill="#4A4A4A"
    {...props}
  >
    {children}
  </text>
);

/**
 * @bookmark
 * Extrapolates the slide number from the current path and adds it to the footer.
 */
var slideNumber = +window.location.pathname.replace('/slides/', '');

// Footer of the slide with the logo and slide number
var SlideFooter = () => (
  <g
    transform={`translate(0, ${slideHeight - footerHeight})`}
    className="slideFooter"
  >
    <line x1={0} y1={0} x2="100%" y2={0} />
    <text x="50%" y={40}>
      {slideNumber}
    </text>
    <image href="../images/pandera-labs.png" x={1135} y={0} width={305} />
  </g>
);

/**
 * @bookmark
 * Header of the slide with the slide title.
 */
var SlideHeader = () => (
  <svg x={0} y={0} width="100%" height={headerHeight} className="slideHeader">
    <rect x={0} y={0} />
    <SlideText x="50%" y="50%">
      {document.querySelector('#slideTitle').innerHTML}
    </SlideText>
  </svg>
);

/**
 * @bookmark
 * Body of the slide (between header and footer).
 */
var SlideBody = (props) => (
  <svg
    x={0}
    y={180}
    width="100%"
    height={slideHeight - headerHeight - footerHeight}
  >
    <g transform="translate(75, 75)">
      {React.Children.map(props.children, (child, index) => (
        <g transform={`translate(0, ${index * 90})`}>
          {child}
        </g>
      ))}
    </g>
  </svg>
);

/**
 * @bookmark
 * Line item in the slide body (level specifies the indentation).
 */
var BulletPoint = props => {
  if (!props.isShown) return null;
  var level = props.level || 0;
  const bulletClass = props.level % 2 ? 'hollowBullet' : 'filledBullet';
  return (
    <g
      transform={`translate(${level * 75}, 0)`}
      className="animated fadeIn"
    >
      <circle cx={0} cy={0} r={5} className={bulletClass} />
      <g transform="translate(25, 0)">
        <SlideText fontFamily="Work Sans" fontSize={props.fontSize || 48}>
          {props.children}
        </SlideText>
      </g>
    </g>
  );
};

/**
 * @bookmark
 * Top-level slide component.
 */
var Slide = React.createClass({
  getInitialState() {
    var shownItems = _.range(1, 7).reduce((acc, item) => ({
      ...acc,
      [item]: false,
    }), {});
    var lastShownItem = '0';
    return {
      shownItems,
      lastShownItem,
    };
  },

/**
 * @bookmark
 * Updates the visibility of the bulleted items when the slide is clicked on.
 */
  handleNavigationClick() {
    var { lastShownItem, shownItems } = this.state;
    var nextItem = +lastShownItem + 1;
    var newShownItems = Object.assign({}, shownItems, { [nextItem]: true });
    this.setState({
      shownItems: newShownItems,
      lastShownItem: nextItem.toString(),
    });
  },

  getIsShown(item) {
    return this.state.shownItems[item];
  },

  render() {
    return (
      <svg
        viewBox={`0 0 1440 ${slideHeight}`}
        onClick={this.handleNavigationClick}
      >
        <SlideHeader />
        <SlideBody>
          <BulletPoint isShown={this.getIsShown('1')}>
            SVG stands for Scalable Vector Graphics
          </BulletPoint>
          <BulletPoint isShown={this.getIsShown('2')}>
            Why use it?
          </BulletPoint>
          <BulletPoint isShown={this.getIsShown('3')} level={1}>
            {"It's neat"}
          </BulletPoint>
          <BulletPoint isShown={this.getIsShown('4')} level={1}>
            {"You're limited only by your imagination"}
          </BulletPoint>
          <BulletPoint isShown={this.getIsShown('5')} level={1}>
            {"It is impossible to misspell"}
          </BulletPoint>
          <BulletPoint isShown={this.getIsShown('6')} fontSize={44}>
            SVG is great because it does exactly what you tell it to do
          </BulletPoint>
          <BulletPoint isShown={this.getIsShown('7')}>
            SVG also sucks because it does exactly what you tell it to do
          </BulletPoint>
        </SlideBody>
        <SlideFooter />
      </svg>
    );
  }
});

/**
 * @bookmark
 * Render the slide.
 */
render(<Slide />);

