      <text className="exampleText" y={lineGap * 3}>{linearScale(105)}</text>
    </g>
  );
};

/**
 * @bookmark
 * An ordinal scale specifies an explicit mapping from a set of
 * data values to a corresponding set of visual attributes (such as colors).
 */
var OrdinalScaleExample = () => {
  var ordinalScale = d3.scaleOrdinal()
    .domain(['poor', 'good', 'great'])
    .range(['red', 'white', 'green']);

  return (
