        {ordinalScale('great')}
      </text>
      <text className="exampleText" y={lineGap * 3}>
        {ordinalScale('poor')}
      </text>
    </g>
  );
};

/**
 * @bookmark
 * For time series data, use a time scale.
 */
var TimeScaleExample = () => {
  var timeScale = d3.scaleTime()
    .domain([new Date(2018, 0, 1), new Date()])
    .range([0, 100]);

  return (
    <g transform="translate(75, 400)">
      <text className="exampleHeader">Time Scale</text>
      <text className="exampleText" y={lineGap * 1}>
        {_.round(timeScale(new Date(2018, 0, 15)), 2)}
